* {
    --font-scale: 1;
    --font-default: Montserrat, sans-serif;

    --default-content-width: 1920px;
    --xlg-content-width: 1140px;
    --lg-content-width: 960px;
    --md-content-width: 720px;
    --sm-content-width: 100%;

    /* H1 */
    --font-type-h1-ff: var(--font-default);
    --font-type-h1-fz: calc(2.25rem * var(--font-scale));
    --font-type-h1-fw: 300;
    --font-type-h1-tt: none;
    --font-type-h1-ls: inherit;
    --font-type-h1-lh: 1.2;

    /* H2 */
    --font-type-h2-ff: var(--font-default);
    --font-type-h2-fz: calc(2rem * var(--font-scale));
    --font-type-h2-fw: 300;
    --font-type-h2-tt: none;
    --font-type-h2-ls: inherit;
    --font-type-h2-lh: 1.2;

    /* H3 */
    --font-type-h3-ff: var(--font-default);
    --font-type-h3-fz: calc(1.75rem * var(--font-scale));
    --font-type-h3-fw: 300;
    --font-type-h3-tt: none;
    --font-type-h3-ls: inherit;
    --font-type-h3-lh: 1.2;

    /* H4 */
    --font-type-h4-ff: var(--font-default);
    --font-type-h4-fz: 1.25rem;
    --font-type-h4-fw: 300;
    --font-type-h4-tt: none;
    --font-type-h4-ls: inherit;
    --font-type-h4-lh: 1.2;

    /* H5 */
    --font-type-h5-ff: var(--font-default);
    --font-type-h5-fz: 1rem;
    --font-type-h5-fw: 400;
    --font-type-h5-tt: none;
    --font-type-h5-ls: inherit;
    --font-type-h5-lh: 1.3;

    /* BODY */
    --font-type-body-ff: var(--font-default);
    --font-type-body-fz: 0.875rem;
    --font-type-body-fw: 300;
    --font-type-body-tt: none;
    --font-type-body-ls: inherit;
    --font-type-body-lh: 2;

    /* BODY 2 */
    --font-type-body2-ff: var(--font-default);
    --font-type-body2-fz: 1rem;
    --font-type-body2-fw: 300;
    --font-type-body2-tt: none;
    --font-type-body2-ls: 1px;
    --font-type-body2-lh: 1.75;

    /* BUTTON */
    --font-type-button-ff: var(--font-default);
    --font-type-button-fz: 1rem;
    --font-type-button-fw: 300;
    --font-type-button-tt: uppercase;
    --font-type-button-ls: inherit;
    --font-type-button-lh: 1.2;

    /* CAPTION */
    --font-type-caption-ff: var(--font-default);
    --font-type-caption-fz: 0.75rem;
    --font-type-caption-fw: 300;
    --font-type-caption-tt: none;
    --font-type-caption-ls: inherit;
    --font-type-caption-lh: 1.4;

    /* OVERLINE */
    --font-type-overline-ff: var(--font-default);
    --font-type-overline-fz: 0.75rem;
    --font-type-overline-fw: 300;
    --font-type-overline-tt: uppercase;
    --font-type-overline-ls: 2px;
    --font-type-overline-lh: 1.3;

    /* COLORS */
    --color-dark-light: #2f2f2f;
    --color-dark-medium: #070606;
    --color-dark-medium-rgb: 21, 21, 21;
    --color-black: #000000;
    --color-white: #ffffff;
    --color-filter-white: invert(100%) sepia(0%) saturate(0%) hue-rotate(0deg)
        brightness(100%) contrast(100%);
    --color-light-medium: #efefef;
    --color-light-dark: #777777;
    --color-accent-medium: #b5923c;
    --color-accent-light: #a89053;
    --color-accent-light-rgb: 168, 144, 83;
    --color-error: #ca0808;

        /* FARBFILTER */

        --color-filter-white: invert(100%) sepia(0%) saturate(0%) hue-rotate(0deg)
        brightness(100%) contrast(100%);
    --color-filter-black: invert(0%) sepia(100%) saturate(7500%)
        hue-rotate(202deg) brightness(108%) contrast(113%);


    /**
      * SPACING
      */
    /* // 0px */
    --space-0x: 0;
    /* // 4px */
    --space-1x: 0.25rem;
    /* // 8px */
    --space-2x: 0.5rem;
    /* // 12px */
    --space-3x: 0.75rem;
    /* // default: 16px */
    --space-4x: 1rem;
    /* // 20px */
    --space-5x: 1.25rem;
    /* // 24px */
    --space-6x: 1.5rem;
    /* // 28px */
    --space-7x: 1.75rem;
    /* // 32px */
    --space-8x: 2rem;
    /* // 36px */
    --space-9x: 2.25rem;
    /* // 40px */
    --space-10x: 2.5rem;
    /* // 44px */
    --space-11x: 2.75rem;
    /* // 48px */
    --space-12x: 3rem;
    /* // 56px */
    --space-14x: 3.5rem;
    /* // 64px */
    --space-16x: 4rem;
    /* // 72px */
    --space-18x: 4.5rem;
    /* // 80px */
    --space-20x: 5rem;
    /* // 84px */
    --space-21x: 5.25rem;
    /* // 88px */
    --space-22x: 5.5rem;
    /* // 96px */
    --space-24x: 6rem;
    /* // 104px */
    --space-26x: 6.5rem;
    /* // 112px */
    --space-28x: 7rem;
    /* // 138px */
    --space-32x: 8rem;
    /* // 144px */
    --space-36x: 9rem;
    /* // 192px */
    --space-48x: 12rem;

    font-family: var(--font-default);
    -webkit-font-smoothing: antialiased;
    hyphens: auto;
    -webkit-hyphens: auto;
}

/* Scaling for mobile */
@container (max-width: 1050px) {
    * {
        --font-scale: 0.8;
    }
}
@container (max-width: 768px) {
    * {
        --font-scale: 0.65;
    }
}

